sysevent_script_action.script:
processDataSource();

function processDataSource(){
	//event.parm1 = file configuration object
	//event.parm2 = attachment record object
	var snLog = new global.GSLog('x_kpmg3_mid_server.MSFT.DEBUG.STATUS', 'MSFI_Process Data Source');
	snLog.includeTimestamp();
	snLog.logInfo('Begin Event Processing');
	snLog.logInfo('Event.parm1'+event.parm1);
	snLog.logInfo('Event.parm2'+event.parm2);
	//Delete any other attachments on the data source
	var otherAttachments = new GlideRecord('sys_attachment');
	otherAttachments.addQuery('table_name', 'sys_data_source');
	otherAttachments.addQuery('table_sys_id', event.parm1.data_source);
	otherAttachments.addQuery('sys_id','!=', event.parm2.sys_id);
	otherAttachments.deleteMultiple();

	//Start loading the data in to an import set
	//gs.log('Loading File ' + sourceDS.name, 'multiftp');
	var loader = new global.GlideImportSetLoader();
	var importSetGr = loader.getImportSetGr(sourceDS);
	var ranload = loader.loadImportSetTable(importSetGr, sourceDS);

	//gs.log('Running Transform map ' + sourceDS.name, 'multiftp');

	//Start the transform on the import set
	var t = new global.GlideImportSetTransformer();
	t.transformAllMaps(importSetGr);
}
